import React from 'react';
import { Table } from './Components/Table'

const user = [{
  id: 1,
  name: 'Arun',
  age: 20,
  email: 'arun@gamil.com',
  address: 'Delhi'
}, {
  id: 2,
  name: 'Shiv',
  age: 20,
  email: 'shiv@gamil.com',
  address: 'Delhi'
}, {
  id: 3,
  name: 'Himanshu',
  age: 20,
  email: 'himanshu@gamil.com',
  address: 'Yamunanagar'
}, {
  id: 4,
  name: 'Harman',
  age: 20,
  email: 'harman@gamil.com',
  address: 'Yamunanagar'
}, {
  id: 5,
  name: 'Malkeet',
  age: 20,
  email: 'malkeet@gamil.com',
  address: 'Yamunanagar'
}]

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      hover: false,
      hoveredData: {}
    }
    // this.testMouseEnter = this.testMouseEnter.bind(this);
    // this.testMouseOut = this.testMouseOut.bind(this);
  }
  // testMouseEnter = (hoverUser) => {
  //   this.setState({
  //     hover: true,
  //     hoveredData: hoverUser
  //   });
  // }

  testMouseEnter(hoverUser){
    this.setState({
      hover: true,
      hoveredData: hoverUser
    });
  }

  testMouseOut = () => {
    this.setState({
      hover: false,
      hoveredData: {}
    })
    console.log('I am Offline');
  }

  render() {
    const { hoveredData } = this.state
    console.log(hoveredData)
    return (
      <div>
        <div>
          <h1 style={{ textAlign: "center", borderBottom: "3px solid" }}>User List Table</h1>
          <Table users={user} onMouseEnter={this.testMouseEnter} onMouseOut={this.testMouseOut} state={this.state.hover ? "onHover" : ""} />
        </div>
        <div className="Hover-div" >
          <table>
            <tbody>
              <tr>
                <td>{hoveredData.id}</td>
                <td>{hoveredData.name}</td>
                <td>{hoveredData.age}</td>
                <td>{hoveredData.email}</td>
                <td>{hoveredData.address}</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    )
  }
}
export default App;
