import React from 'react';
import {Table } from './Components/Table'
import OnHover from './Components/OnHover';

const user = [{
        id : 1,
        name : 'Arun',
        age : 20,
        email : 'arun@gamil.com',
        address : 'Delhi'
},{
        id : 2,
        name : 'Shiv',
        age : 20,
        email : 'shiv@gamil.com',
        address : 'Delhi'
},{
        id : 3,
        name : 'Himanshu',
        age : 20,
        email : 'himanshu@gamil.com',
        address : 'Yamunanagar'
},{
  id : 4,
  name : 'Harman',
  age : 20,
  email : 'harman@gamil.com',
  address : 'Yamunanagar'
},{
  id : 5,
  name : 'Malkeet',
  age : 20,
  email : 'malkeet@gamil.com',
  address : 'Yamunanagar'
}]

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
        hover : false 
    }
    this.testMouseEnter = this.testMouseEnter.bind(this);
    this.testMouseOut = this.testMouseOut.bind(this);
}
testMouseEnter = () =>{
  this.setState({
      hover : true 
  });
}
testMouseOut = () =>{
   this.setState({
       hover: false 
   });
}
hoverRender(userdetail) {
   return (
       <tr>
           <td>{userdetail.id}</td>
           <td>{userdetail.name}</td>
           <td>{userdetail.age}</td>
           <td>{userdetail.email}</td>
           <td>{userdetail.address}</td>
       </tr>
   );
} 

render() {
  return (
    <div>
      <h1  style={{textAlign: "center", borderBottom:"3px solid"}}>User List Table</h1>
      <Table  users={user} onMouseOver={this.testMouseEnter} />
    </div>);
  }
}

export default App;
